import React, { useState } from "react";
import useInterval from "../utils/useInterval";
import FocusDuration from "./FocusDuration";
import BreakDuration from "./BreakDuration";
import Session from "./Session";
import StartStop from "./StartStop";

function Pomodoro() {
// .............
// initial state values
// .............
      const initialDurationStates = {
        focusDuration: 25 * 60,
        breakDuration: 5 * 60,
        // focusDuration: 3,
        // breakDuration: 5,
      }
      const initialIsSessionFocus = true;
      const initialIsTimerRunning = false;
      const initialIsTimerStarted = false;

// .............
// State Declarations
// .............
// const [durations, setDurations] = useState({...initialDurationStates})
const [isSessionFocus, setIsSessionFocus] = useState(initialIsSessionFocus);
const [remainingSeconds, setRemainingSeconds] = useState(initialDurationStates.focusDuration);
const [isTimerRunning, setIsTimerRunning] = useState(initialIsTimerRunning);
const [isTimerStarted, setIsTimerStarted] = useState(initialIsTimerStarted);

// .............
// functions and helper functions
// .............

useInterval(
  () => {
    // To-Do: Implement what should happen when the timer is running
    setRemainingSeconds((current) => current - 1);
    remainingSeconds === 0 && switchSessions();
  },
  isTimerRunning ? 1000 : null
  );

  function playPause() {
    setIsTimerRunning((prevState) => !prevState);
    setIsTimerStarted(() => true);
  }
  function switchSessions(){
    new Audio(`https://bigsoundbank.com/UPLOAD/mp3/1482.mp3`).play();
    setIsSessionFocus((previousState) => !previousState);
    isSessionFocus ? setRemainingSeconds(durations.breakDuration) : setRemainingSeconds(durations.focusDuration);
  }

  function updateRemainingFromDuration(duration){
    setRemainingSeconds(duration)
  }

  const displaySession= isTimerStarted && (
    <Session 
          durations={durations} 
          isSessionFocus={isSessionFocus} 
          remainingSeconds={remainingSeconds}
        />
  );


// .............
// Handler functions
// .............

  const stopHandler = () => {
    setIsTimerRunning(false);
    setIsSessionFocus(true);
    setRemainingSeconds(durations.focusDuration);
    setIsTimerStarted(() => false);
  }

  const focusDecreaseHandler = () => {
      const newDuration = Math.max(300, durations.focusDuration - 300); 
      setDurations({
        ...durations,
        focusDuration: newDuration,
      });
      isSessionFocus && updateRemainingFromDuration(newDuration);
  }
  const focusIncreaseHandler = () => {
      const newDuration = Math.min(3600, durations.focusDuration + 300); 
      setDurations({
        ...durations,
        focusDuration: newDuration,
      });
      isSessionFocus && updateRemainingFromDuration(newDuration);
  }
  const breakDecreaseHandler = () => {
      const newDuration = Math.max(60, durations.breakDuration - 60)
      setDurations({
        ...durations,
        breakDuration: newDuration, 
      });
      !isSessionFocus && updateRemainingFromDuration(newDuration);
  }
  const breakIncreaseHandler = () => {
      const newDuration =  Math.min(900, durations.breakDuration + 60); 
      setDurations({
        ...durations,
        breakDuration: newDuration,
      });
      !isSessionFocus && updateRemainingFromDuration(newDuration);
  }

  const durationHandler = {
    focus: {decrease: focusDecreaseHandler, increase: focusIncreaseHandler},
    break: {decrease: breakDecreaseHandler, increase: breakIncreaseHandler}
  }


// .............
// Render
// .............

  return (
    <div className="pomodoro">
      <div className="row">
        <FocusDuration 
          durations={durations} 
          durationHandler={durationHandler} 
          isTimerStarted={isTimerStarted}
        />
        <BreakDuration 
          durations={durations} 
          durationHandler={durationHandler} 
          isTimerStarted={isTimerStarted}
        />
      </div>
      <StartStop 
        playPause={playPause}
        isTimerRunning={isTimerRunning}
        isTimerStarted={isTimerStarted}
        stopHandler={stopHandler}
      />
      {displaySession}
    </div>
  );
}

export default Pomodoro;
