import React, { useState } from "react";
import classNames from "../utils/class-names";
import useInterval from "../utils/useInterval";
import { minutesToDuration, secondsToDuration } from "../utils/duration";
import Durations from "./Durations";
import Duration from "./Duration";

function Pomodoro() {
// .............
// initial state values
// .............
      const initialDurationStates = {
        focusDuration: 25 * 60,
        breakDuration: 5 * 60,
      }
// .............
// State Declarations
// .............
const [durations, setDurations] = useState({...initialDurationStates})
const [isSessionFocus, setIsSessionFocus] = useState(true);
const [remainingSeconds, setRemainingSeconds] = useState(initialDurationStates.focusDuration);
const [isTimerRunning, setIsTimerRunning] = useState(false);

// .............
// functions and helper functions
// .............

useInterval(
  () => {
    // ToDo: Implement what should happen when the timer is running

    setRemainingSeconds((current) => current - 1);
    console.log("is timer running: " + isTimerRunning);
    console.log(remainingSeconds);
  },
  isTimerRunning ? 1000 : null
  );

  function playPause() {
    setIsTimerRunning((prevState) => !prevState);
  }

// .............
// Handler functions
// .............
  function updateRemainingFromDuration(duration){
    console.log(duration);
  }

  const focusDecreaseHandler = () => {
    !isTimerRunning && setDurations({
      ...durations,
      focusDuration: Math.max(300, durations.focusDuration - 300),
    });
    isSessionFocus && updateRemainingFromDuration(durations.focusDuration - 300)
  }
  const focusIncreaseHandler = () => {
    !isTimerRunning && setDurations({
      ...durations,
      focusDuration: Math.min(3600, durations.focusDuration + 300),
    });
  }
  const breakDecreaseHandler = () => {
    !isTimerRunning && setDurations({
      ...durations,
      breakDuration: Math.max(60, durations.breakDuration - 60),
    });
  }
  const breakIncreaseHandler = () => {
    !isTimerRunning && setDurations({
      ...durations,
      breakDuration: Math.min(900, durations.breakDuration + 60),
    });
  }

  const durationHandler = {
    focus: {decrease: focusDecreaseHandler, increase: focusIncreaseHandler},
    break: {decrease: breakDecreaseHandler, increase: breakIncreaseHandler}
  }

// console.log("is timer running: " + isTimerRunning);

// .............
// Render
// .............

  return (
    <div className="pomodoro">
      <Durations durations={durations} durationHandler={durationHandler} />
      <div className="row">
        <div className="col">
          <div
            className="btn-group btn-group-lg mb-2"
            role="group"
            aria-label="Timer controls"
          >
            <button
              type="button"
              className="btn btn-primary"
              data-testid="play-pause"
              title="Start or pause timer"
              onClick={playPause}
            >
              <span
                className={classNames({
                  oi: true,
                  "oi-media-play": !isTimerRunning,
                  "oi-media-pause": isTimerRunning,
                })}
              />
            </button>
            {/* TODO: Implement stopping the current focus or break session and disable when there is no active session */}
            <button
              type="button"
              className="btn btn-secondary"
              title="Stop the session"
            >
              <span className="oi oi-media-stop" />
            </button>
          </div>
        </div>
      </div>
      <div>
        {/* TODO: This area should show only when a focus or break session is running or pauses */}
        <div className="row mb-2">
          <div className="col">
            {/* TODO: Update message below to include current session (Focusing or On Break) and total duration */}
            <h2 data-testid="session-title">Focusing for 25:00 minutes</h2>
            {/* TODO: Update message below to include time remaining in the current session */}
            <p className="lead" data-testid="session-sub-title">
              {secondsToDuration(remainingSeconds)} remaining
            </p>
          </div>
        </div>
        <div className="row mb-2">
          <div className="col">
            <div className="progress" style={{ height: "20px" }}>
              <div
                className="progress-bar"
                role="progressbar"
                aria-valuemin="0"
                aria-valuemax="100"
                aria-valuenow="0" // TODO: Increase aria-valuenow as elapsed time increases
                style={{ width: "0%" }} // TODO: Increase width % as elapsed time increases
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Pomodoro;
