import React, { useState } from "react";
import useInterval from "../utils/useInterval";
import FocusDuration from "./FocusDuration";
import BreakDuration from "./BreakDuration";
import Session from "./Session";
import StartStop from "./StartStop";

function Pomodoro() {
// .............
// initial state values
// .............
      const initialStates = {
        //----Testing Initial Values-----
        focusDuration: 10,
        breakDuration: 8,
        remainingSeconds: 10,
        // focusDuration: 25 * 60,
        // breakDuration: 5 * 60,
        // remainingSeconds: 25 * 60,
        //-----/Testing Initial Values-----
        isTimerRunning: false,
        isTimerStarted: false,
        isSessionFocus: true,
      }
console.log('hi');
// .............
// State Declarations
// .............
const [state, setState] = useState({...initialStates})


// .............
// functions and helper functions
// .............

useInterval(
  () => {
    // To-Do: Implement what should happen when the timer is running
    setState((current) => ({...current, remainingSeconds: current.remainingSeconds -1}));
    state.remainingSeconds === 0 && switchSessions();
  },
  state.isTimerRunning ? 1000 : null
  );

  function playPause() {
    setState((current) => ({...current, isTimerRunning: !current.isTimerRunning}));
    setState((current) => ({...current, isTimerStarted: true}));

  }
  function switchSessions(){
    new Audio(`https://bigsoundbank.com/UPLOAD/mp3/1482.mp3`).play();
    setState((current) => ({...current, isSessionFocus: !current.isSessionFocus}));
    state.isSessionFocus 
      ? setState((current) => ({...current, remainingSeconds: current.breakDuration})) 
      : setState((current) => ({...current, remainingSeconds: current.focusDuration})); 

  }

  const displaySession= state.isTimerStarted && (
    <Session 
          state={state} 
        />
  );


// .............
// Handler functions
// .............

  const stopHandler = () => {
    setState((current) => ({
      ...initialStates,
      focusDuration: current.focusDuration,
      breakDuration: current.breakDuration,
      remainingSeconds: current.focusDuration,
    }));
  }

  const focusDecreaseHandler = () => {
      const newDuration = Math.max(300, state.focusDuration - 300); 
      setState((current) => ({
        ...current,
        focusDuration: newDuration,
        remainingSeconds: newDuration,
      }));
  }
  const focusIncreaseHandler = () => {
      const newDuration = Math.min(3600, state.focusDuration + 300); 
      setState((current) => ({
        ...current,
        focusDuration: newDuration,
        remainingSeconds: newDuration,
      }));
  }
  const breakDecreaseHandler = () => {
      const newDuration = Math.max(60, state.breakDuration - 60)
      setState((current) => ({
        ...current,
        breakDuration: newDuration,
      }));
  }
  const breakIncreaseHandler = () => {
      const newDuration =  Math.min(900, state.breakDuration + 60); 
      setState((current) => ({
        ...current,
        breakDuration: newDuration,
      }));
  }

  const durationHandler = {
    focus: {decrease: focusDecreaseHandler, increase: focusIncreaseHandler},
    break: {decrease: breakDecreaseHandler, increase: breakIncreaseHandler}
  }


  // const durationHandler = (event) => {
  //   const target = event.target.type === "button" ? event.target : event.target.parentNode;
  //   const adjuster = target.getAttribute("adjuster"); 
  //   console.log(adjuster);
  //   let newDuration = 0;
  //   switch(adjuster) {
  //     case "decreaseFocus":
  //          newDuration = Math.max(300, state.focusDuration - 300);
  //       break;
  //     case "increaseFocus":
  //          newDuration = Math.min(3600, state.focusDuration + 300);
  //       break;
  //     case "decreaseBreak":
  //          newDuration = Math.max(60, state.breakDuration - 60);
  //       break;
  //     case "increaseBreak":
  //          newDuration =  Math.min(900, state.breakDuration + 60);
  //       }
  //       console.log(newDuration);
  //       setState((current) => ({
  //         ...current,
  //         breakDuration: newDuration,
  //       }));
  // }






// .............
// Render
// .............

  return (
    <div className="pomodoro">
      <div className="row">
        <FocusDuration 
          state={state} 
          durationHandler={durationHandler} 
        />
        <BreakDuration 
          state={state} 
          durationHandler={durationHandler} 
        />
      </div>
      <StartStop 
        state={state}
        playPause={playPause}
        stopHandler={stopHandler}
      />
      {displaySession}
    </div>
  );
}

export default Pomodoro;
