import React from "react";
import { secondsToDuration } from "../utils/duration";
import ProgressBar from "./ProgressBar";

function Session(durations, isSessionFocus, remainingSeconds){

    function sessionTitle() {
        if(isSessionFocus) {
          return "Focusing for " + secondsToDuration(durations.focusDuration) + " minutes";
        } else {
          return "On Break for " + secondsToDuration(durations.breakDuration) + " minutes";
        }
      }
/* TODO: This area should show only when a focus or break session is running or pauses */
      
    return (
        <>
        <div className="row mb-2">
            <div className="col">
                {/* TO-DO: Update message below to include current session (Focusing or On Break) and total duration */}
                <h2 data-testid="session-title">{sessionTitle()}</h2>
                {/* TO-DO: Update message below to include time remaining in the current session */}
                <p className="lead" data-testid="session-sub-title">
                {secondsToDuration(remainingSeconds)} remaining
                </p>
            </div>
            </div>
            <ProgressBar 
            durations={durations} 
            isSessionFocus={isSessionFocus} 
            remainingSeconds={remainingSeconds} />
        </>
    )

}

export default Session;
